package demo;

public class Datatype {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//Primitive Datatypes
		byte a = 23;
		short b = 12;
		int c = 13;
		long d = 12562345L;
		float e = 45.5f;
		double f = 23.45;
		System.out.println(a+" "+b+" "+c+" "+d+" "+e+" "+f);
		int arr[] = new int[5];
		arr[0] = 2;
		arr[1] = 12;
		arr[2] = 22;
		arr[3] = 32;
		
		System.out.println(arr[0]);
	}

}
